import React from 'react';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import { UncontrolledTooltip } from 'reactstrap';

import { updateIncome } from '../../actions';


const toolTipText = [
    { field: 'rent', tip: 'Enter any rent paid to the recipient in this field.'},
    { field: 'royalties', tip: 'Enter any royalties paid to the recipient in this field.'},
    { field: 'other', tip: 'Enter any other income paid to the recipient in this field.'},
    { field: 'federal_tax_withheld', tip: 'Enter the amount of Federal income tax withheld from disbursements to recipient in this field.'},
    { field: 'non_employee_comp', tip: 'Enter any non-employee compensation paid to the recipient in this field.  This will be reported in Box 7.     This includes professional service fees, such as fees to attorneys (including corporations), accountants, architects, contractors, engineers, designers, software developers (if not paid as employees), etc.'},
    { field: 's409a_income', tip: 'Enter any Section 409A income paid to the recipient in this field..'},
    { field: 's409a_deferrals', tip: 'Enter any Section 409A deferrals paid to the recipient in this field.'},
    { field: 'direct_sales', tip: 'Payer made direct sales of $5,000 or more of consumer products to recipient for sale.'},
    { field: 'crop_insurance', tip: 'Enter any other income paid to the recipient in this field.'},
    { field: 'state_tax_withheld', tip: 'Enter the amount state income tax withheld in this field.'},
    { field: 'state_tax_id', tip: 'Enter the payer\'s or employer\'s state tax ID in this field.'},
    { field: 'state_income', tip: 'Enter the recipients state income in this field.'},
]


class GetIncome extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            rent: null,
            royalties: null,
            other: null,
            federal_tax_withheld: null,
            non_employee_comp: null,
            s409a_income: null,
            s409a_deferrals: null,
            fish_boat: null,
            medical_payments: null,
            substitute_payments: null,
            direct_sales: false,
            state_tax_withheld: null,
            state_tax_id: null,
            state_income: null,

            isValidIncome: false,
            isTouchedRent: false,
            isTouchedRoyalties: false,
            isTouchedOther: false,
            isTouchedFedTax: false,
            isTouchedNonEmpComp: false,
            isTouchedS409Income: false,
            isTouchedS409Deferrals: false,
            isTouchedFishBoat: false,
            isTouchedMedPayments: false,
            isTouchedSubPayments: false,
            ///
            isTouchedDirectSales: false,
            isTouchedStateTax: false,
            isTouchedStateTaxId: false,
            isTouchedStateIncome: false,
        }
        this.handleFieldChange = this.handleFieldChange.bind(this);
        this.handleRentBlur = this.handleRentBlur.bind(this);
        this.handleRoyaltiesBlur = this.handleRoyaltiesBlur.bind(this);
        this.handleOtherBlur = this.handleOtherBlur.bind(this);
        this.handleFedTaxBlur = this.handleFedTaxBlur.bind(this);
        this.handleNonEmpCompBlur = this.handleNonEmpCompBlur.bind(this);
        this.handleS409IncBlur = this.handleS409IncBlur.bind(this);
        this.handleS409DefBlur = this.handleS409DefBlur.bind(this);
        this.handleStateTaxBlur = this.handleStateTaxBlur.bind(this);
        this.handleStateIDBlur = this.handleStateIDBlur.bind(this);
        this.handleStateIncomeBlur = this.handleStateIncomeBlur.bind(this);
        this.handleDirectSalesBlur = this.handleDirectSalesBlur.bind(this);
    }

    handleRentBlur(e) {
        const value = e.target.value;   
        this.setState({ rent:value, isTouchedRent:true });
    };

    handleRoyaltiesBlur(e) {
        const value = e.target.value;   
        this.setState({ royalties:value, isTouchedRoyalties:true });
    }

    handleOtherBlur(e) {
        const value = e.target.value;   
        this.setState({ other:value, isTouchedOther:true });
    }

    handleFedTaxBlur(e) {
        const value = e.target.value;   
        this.setState({ federal_tax_withheld:value, isTouchedFedTax:true });
    }

    handleNonEmpCompBlur(e) {
        const value = e.target.value;   
        this.setState({ non_employee_comp:value, isTouchedNonEmpComp:true });
    }

    handleS409IncBlur(e) {
        const value = e.target.value;   
        this.setState({ s409a_income:value, isTouchedS409Income:true });
    }

    handleS409DefBlur(e) {
        const value = e.target.value;   
        this.setState({ s409a_deferrals:value, isTouchedS409Deferrals:true });
    }

    handleStateTaxBlur(e) {
        const value = e.target.value;   
        this.setState({ state_tax_withheld:value, isTouchedStateTax:true });
    }

    handleStateIDBlur(e) {
        const value = e.target.value;   
        this.setState({ state_tax_id:value, isTouchedStateTaxId:true });
    }

    handleStateIncomeBlur(e) {
        const value = e.target.value;   
        this.setState({ state_income:value, isTouchedStateIncome:true });
    }

    handleDirectSalesBlur(e) {
        const value = e.target.value;   
        this.setState({ direct_sales:value, isTouchedDirectSales:true });
    }

    handleFieldChange(e, fieldname) {
        const value = e.target.value;   
        switch(fieldname) {
            case 'rent': 
                this.setState({ rent:value, isTouchedRent:true }, () => {
                    this.props.updateIncome(this.state)
                });
                break;
            case 'royalties': 
                this.setState({ royalties:value, isTouchedRoyalties: true }, () => {
                    this.props.updateIncome(this.state)
                });
                break;
            case 'other': 
                this.setState({ other:value, isTouchedOther: true }, () => {
                    this.props.updateIncome(this.state)
                });
                break;
            case 'federal_tax_withheld': 
                this.setState({ federal_tax_withheld:value, isTouchedFedTax: true }, () => {
                    this.props.updateIncome(this.state)
                });
                break;
            case 'non_employee_comp': 
                this.setState({ non_employee_comp:value, isTouchedNonEmpComp: true }, () => {
                    this.props.updateIncome(this.state)
                });
                break;
            case 's409a_income': 
                this.setState({ s409a_income:value, isTouchedS409Income: true }, () => {
                    this.props.updateIncome(this.state)
                });
                break;
            case 's409a_deferrals': 
                this.setState({ s409a_deferrals:value, isTouchedS409Deferrals: true }, () => {
                    this.props.updateIncome(this.state)
                });
                break;
            case 'state_income': 
                this.setState({ state_income:value, isTouchedStateIncome: true }, () => {
                    this.props.updateIncome(this.state)
                });
                break;
            case 'state_tax_id': 
                this.setState({ state_tax_id:value, isTouchedStateTaxId: true }, () => {
                    this.props.updateIncome(this.state)
                });
                break;
            case 'direct_sales': 
                this.setState({ direct_sales:value, isTouchedDirectSales: true }, () => {
                    this.props.updateIncome(this.state)
                });
                break;
            default:;
        }
    }

    getToolTip(props) {
        // props = field name
        // filter toolTipText array to isolate the one in props
        const tip = toolTipText.filter( el => el.field === props );
        if (tip[0]) {
            return <div> {tip[0].tip} </div> ;
        }

        return <div> Please enter the appropriate information </div> ;
    };


    renderToolTip = (props) => {
        return(
            <UncontrolledTooltip placement="right" target={props.input} >
                {this.getToolTip(props.field) }
            </UncontrolledTooltip>
        )
    };

    render() {
    return (
        <div>
            <div className="input-group" align="left">
                <h4>Income Information</h4>
            </div>

            <div align="left">
                <small>Rent</small>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">$</span>
                    </div>
                    <input type="number" id="inputRent" className="form-control" 
                        onChange={(e) => this.handleFieldChange(e, "rent")} 
                        onBlur={(e) => this.handleRentBlur(e)}
                        />
                    {this.renderToolTip( {field:"rent", input: "inputRent"} )}
                </div>
            </div>


            <div align="left">
                <small>Royalties</small>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">$</span>
                    </div>
                    <input className="form-control" type="number" id="inputRoyalties" 
                        onChange={(e) => this.handleFieldChange(e, "royalties")} 
                        onBlur={(e) => this.handleRoyaltiesBlur(e)}
                    />
                    {this.renderToolTip( {field:"royalties", input: "inputRoyalties"} )}
                </div>
            </div>


            <div align="left">
                <small>Other Income</small>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">$</span>
                    </div>
                    <input className="form-control" type="number" id="inputOtherIncome" 
                        onChange={(e) => this.handleFieldChange(e, "other")} 
                        onBlur={(e) => this.handleOtherBlur(e)}
                    />
                    {this.renderToolTip( {field:"other", input: "inputOtherIncome"} )}
                </div>
            </div>


            <div align="left">
                <small>Federal Income Tax Withheld</small>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">$</span>
                    </div>
                    <input type="number" className="form-control" id="inputFedTax" 
                        onChange={(e) => this.handleFieldChange(e, "federal_tax_withheld")} 
                        onBlur={(e) => this.handleFedTaxBlur(e)}
                    />
                    {this.renderToolTip( {field:"federal_tax_withheld", input: "inputFedTax"} )}
                </div>
            </div>


            <div align="left">
                <small>Non-Employee Compensation</small>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">$</span>
                    </div>
                    <input type="number" className="form-control" id="inputNonEmployee"
                        onChange={(e) => this.handleFieldChange(e, "non_employee_comp")} 
                        onBlur={(e) => this.handleNonEmpCompBlur(e)}
                    />
                    {this.renderToolTip( {field:"non_employee_comp", input: "inputNonEmployee"} )}
                </div>
            </div>


            <div align="left">
                <small>Section 409A Income</small>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">$</span>
                    </div>
                    <input type="number" id="input409Income" className="form-control"  
                        onChange={(e) => this.handleFieldChange(e, "s409a_income")} 
                        onBlur={(e) => this.handleS409IncBlur(e)}
                    />
                    {this.renderToolTip( {field:"s409a_income", input: "input409Income"} )}
                </div>
            </div>


            <div align="left">
                <small>Section 409A Deferrals</small>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">$</span>
                    </div>
                    <input type="number" id="input409Deferrals" className="form-control" 
                        onChange={(e) => this.handleFieldChange(e, "s409a_deferrals")} 
                        onBlur={(e) => this.handleS409DefBlur(e)}
                    />
                    {this.renderToolTip( {field:"s409a_deferrals", input: "input409Deferrals"} )}
                </div>
            </div>


            <div align="left">
                <small>State Tax Withheld</small>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">$</span>
                    </div>
                    <input type="number" id="inputStateTax" className="form-control" 
                        onChange={(e) => this.handleFieldChange(e, "state_tax_withheld")} 
                        onBlur={(e) => this.handleNonEmpCompBlur(e)}
                    /> 
                    {this.renderToolTip( {field:"state_tax_withheld", input: "inputStateTax"} )}
                </div>
            </div>


            <div align="left">
                <small>Payer's State Tax ID</small>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">12-3...</span>
                    </div>
                    <input type="number" id="inputStateTaxID" className="form-control" 
                        onChange={(e) => this.handleFieldChange(e, "state_tax_id")} 
                        onBlur={(e) => this.handleNonEmpCompBlur(e)}
                    />
                    {this.renderToolTip( {field:"state_tax_id", input: "inputStateTaxID"} )}
                </div>
            </div>


            <div align="left">
                <small>State Income</small>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">$</span>
                    </div>
                    <input type="number" id="inputStateIncome" className="form-control" 
                        onChange={(e) => this.handleFieldChange(e, "state_income")} 
                        onBlur={(e) => this.handleNonEmpCompBlur(e)}
                    />
                    {this.renderToolTip( {field:"state_income", input: "inputStateIncome"} )}
                </div>
            </div>


            <div align="left">
                <small>Direct sales of $5,000 or more</small>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <div className="input-group-text">
                            <input type="checkbox" aria-label="Checkbox for following text input"
                                onChange={(e) => this.handleFieldChange(e, "direct_sales")} 
                                onBlur={(e) => this.handleDirectSalesBlur(e)}
                            />
                        </div>
                    </div>
                    <input type="readonly" className="form-control" id="chkbxDirectSales" />
                    {this.renderToolTip( {field:"direct_sales", input: "chkbxDirectSales"} )}
                </div>
            </div>

        </div>
    )
    }
}


function mapStateToProps(state) {
    return{
        payer: state.income
    }
}

const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({
        updateIncome: updateIncome
    }, dispatch )
}

GetIncome = connect(mapStateToProps, mapDispatchToProps)(GetIncome);

export default GetIncome;
