import React, { Component } from 'react';
import { Link } from 'react-router-dom';

import {CardElement, CardNumberElement, CardExpiryElement, CardCVCElement, injectStripe} from 'react-stripe-elements';

import axios from "axios";





class CheckoutPage2 extends Component {

	constructor(props) {
		super(props);
		this.submit = this.submit.bind(this);
	}


	async submit(e) {
		//Get Token
		let {token} = await this.props.stripe.createToken({name: "Name"});

		console.log(token)
		// let response = await fetch("/charge", {
		//   method: "POST",
		//   headers: {"Content-Type": "text/plain"},
		//   body: token.id
		// });

		// if (response.ok) console.log("Purchase Complete!")



		let body = token.id
		let API = 'https://fast1099.com:10443/charge'
        var headers = {
            'Content-Type': 'application/x-www-form-urlencoded',
            "Access-Control-Allow-Origin": "*" 
        }


        let response_status
        let charge_id


		await axios.post(API, body, headers)
			.then(function (response) {
				//handle success
				console.log(response.status);
				if (response.status === 200) {
					response_status = response.status;
					charge_id = response.data.response.id;
				}
				console.log(response);
			})	
			.catch(function (response) {
				//handle error
				// display error in UI
				console.log(response);
			});


		//LOG RESPONSE
		if (response_status === 200) {
			this.setState({charge_id: charge_id});
			console.log("success");
		}



	}


	render(){


		return (
	      <div className="checkout">

	        <CardNumberElement/>
	        <CardExpiryElement/>
	        <CardCVCElement/>

	        <button onClick={this.submit}>Send</button>
	      </div>
		)


	}

}



export default injectStripe(CheckoutPage2);
