import { UPDATE_RECIPIENT } from "../actions/action-types";

const recipientState = {
    name: null,
    tin: null,
    street: null,
    city: null,
    state: null,
    zipcode: null,
    account_nbr: null,
    FATCA: false,

    isValidName: false,
    isValidTIN: false,
    isValidZip: false,
    isValidAddress: false,
    isRequiredName: true,
    isRequiredTIN: true,
    isRequiredZip: true,
    isRequiredAddress: true
}

  export default function recipientReducer(state = recipientState, action) {
    switch( action.type ) {
        case UPDATE_RECIPIENT: 
            return action.payload 

        default:
            return state;
    }
};
