import React from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';

import { updateTaxYear } from '../../actions';


const years = [
    {
      id: 1,
      year: 2019
    },
    {
      id: 2,
      year: 2018
    },
    {
      id: 3,
      year: 2017
    },
    {
      id: 4,
      year: 2016
    },
    {
      id: 5,
      year: 2015
    },
    {
      id: 6,
      year: 2014
    }
  ]

class GetTaxYear extends React.Component {
  state = {
    year: 2019,
    isVoid: false,
    isCorrected: false
  };

  componentDidMount(){
    this.props.updateTaxYear(this.state)
  }

  handleFieldChange(e, fieldname) {
    const val = e.target.value;
    //console.log(val);

    switch( fieldname ) {
      case "year":
        this.setState({ year:val }, () => {
              this.props.updateTaxYear(this.state)
          });
        break;
      case "void":
          const newval = (this.state.isVoid === 'on' && val === 'on') ? null : 'on'
          this.setState({ isVoid:newval }, () => {
              this.props.updateTaxYear(this.state)
          });
        break;
      case "corrected":
          const newval2 = (this.state.isCorrected === 'on' && val === 'on') ? null : 'on'
          this.setState({ isCorrected:newval2 }, () => {
              this.props.updateTaxYear(this.state)
          });
        break;
      default: ;
      }
      console.log(this.state);
    }

  render() {
    const year = years.map( el => <option key={el.id}> {el.year}</option>)





    return (
        <div>
          <div className="form-group" align="left">
            <label htmlFor="selectTaxYear"><h4>Tax Year</h4></label>
            <select className="form-control" id="selectTaxYear"
                onChange={(e) => this.handleFieldChange(e, "year")} 
              >
              {year}
            </select>
          </div>

          <br/>


          <div className="form-check form-check-inline" align="left" >
            <input className="form-check-input" type="checkbox" id="voidCheck" 
                onChange={(e) => this.handleFieldChange(e, "void")} 
                />
            <label className="form-check-label" htmlFor="voidCheck">VOID</label>
          </div>
          <div className="form-check form-check-inline" align="right" >
            <input className="form-check-input" type="checkbox" id="correctedCheck" 
                onChange={(e) => this.handleFieldChange(e, "corrected")} 
                />
            <label className="form-check-label" htmlFor="correctedCheck">CORRECTED</label>
          </div>



      </div>
    )
  }
}







function mapStateToProps(state) {
  return{
    year: state.year
  }
}

const mapDispatchToProps = (dispatch) => {
  return bindActionCreators({
      updateTaxYear: updateTaxYear
  }, dispatch )
}

GetTaxYear = connect(mapStateToProps, mapDispatchToProps)(GetTaxYear);

export default GetTaxYear;
