import React from 'react'
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import ReactTooltip from 'react-tooltip'

import { updateRecipient } from '../../actions';



class GetRecipientInfo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

            name: '',
            nameError:'',
            isTouchedName: false,

            tin: '',
            tinError:'',
            isTouchedTin: false,

            zipcode: '',
            zipcodeError:'',
            isTouchedZipcode: false,

            street: '',
            streetError:'',
            isTouchedStreet: false,

            city: '',
            cityError:'',
            isTouchedCity: false,

            state: '',
            stateError:'',
            isTouchedState: false,

            account_nbr: '',
            account_nbrError:'',
            isTouchedAccount_nbr: false,

            FACTA: '',
            FACTAError:'',
            isTouchedFACTA: false,
        }


        this.handleNameBlur = this.handleNameBlur.bind(this);
        this.handleNameChange = this.handleNameChange.bind(this);

        this.handleTinBlur = this.handleTinBlur.bind(this);
