import React from 'react';

import store from '../../store';


class ErrorList extends React.Component {
    state = {
        payer: {},
        recipient: {},
        income: {},
        email: {}
    }

    updateErrors() {
        const payerErrors = store.getState().payerErrors;
        this.setState({ payer:payerErrors });
        const recipientErrors = store.getState().recipientErrors;
        this.setState({ recipient:recipientErrors });
        const incomeErrors = store.getState().incomeErrors;
        this.setState({ income:incomeErrors });
        const emailErrors = store.getState().emailErrors;
        this.setState({ email:emailErrors });
    }

    componentDidMount () {
		this.unsubscribe = store.subscribe(this.updateErrors.bind(this))
    }

    componentWillUnmount() {
        this.unsubscribe();
    }

    render() {
        if( !this.state.payer && !this.state.recipient && !this.state.email && !this.state.income ){
            return <div></div>;
        }

        if( !this.state.payer.errCount && !this.state.recipient.errCount && !this.state.email.errCount && !this.state.income.errCount ){
            return <div></div>;
        }

        if( this.state.payer.errCount===0 && this.state.recipient.errCount===0 && this.state.email.errCount===0 && this.state.income.errCount===0 ) {
            return <div></div>;
        }

        return(
            <div>
                <p></p>
                <p></p>
                <div className="alert alert-danger" align="left">
                    <ul>
                        {this.state.payer.name ? <li>{this.state.payer.name}</li> : ''}
                        {this.state.payer.tin  ? <li>{this.state.payer.tin}</li> : ''}
                        {this.state.payer.street? <li>{this.state.payer.street}</li> : ''}
                        {this.state.payer.zipcode? <li>{this.state.payer.zipcode}</li> : ''}
                        {this.state.payer.phone? <li>{this.state.payer.phone}</li> : ''}
                        {this.state.recipient.name ? <li>{this.state.recipient.name}</li> : ''}
                        {this.state.recipient.tin  ? <li>{this.state.recipient.tin}</li> : ''}
                        {this.state.recipient.street? <li>{this.state.recipient.street}</li> : ''}
                        {this.state.recipient.zipcode? <li>{this.state.recipient.zipcode}</li> : ''}
                        {this.state.recipient.account? <li>{this.state.recipient.account}</li> : ''}
                        {this.state.income.income? <li>{this.state.income.income}</li> : '' }
                        {this.state.email.email? <li>{this.state.email.email}</li> : '' }
                    </ul>
                </div>
            </div>
        )
    }
}


export default ErrorList;
