import { UPDATE_PAYER } from "../actions/action-types";

const payerState = {
    name: null,
    tin: null,
    street: null,
    city: null,
    state: null,
    zipcode: null,
    phone: null,

    isValidName: false,
    isValidTIN: false,
    isValidAddress: false,
    isValidZip: false,
    isValidPhone: false
    // isValid: {
    //     name: false,
    //     tin: false,
    //     street: false,
    //     zipcode: false,
    //     phone: false
    //     },
    // isRequired: {
    //     name: true,
    //     tin: true,
    //     zip: true,
    //     address: true
    // }
}

export default function payerReducer(state = payerState, action) {
    switch( action.type ) {
        case UPDATE_PAYER: 
            return action.payload 

        default:
            return state;
    }
};
