import React from 'react'
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import ReactTooltip from 'react-tooltip'

import { FieldCheck3 } from './FieldCheck';
import { updateRecipient } from '../../actions';

import store from '../../store';
import recipientValidator from '../../validate/recipientValidator';


//function GetRecipientInfo() {
class GetRecipientInfo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

            name: '',
            nameError:'',
            isTouchedName: false,

            tin: '',
            tinError:'',
            isTouchedTin: false,

            zipcode: '',
            zipcodeError:'',
            isTouchedZipcode: false,

            street: '',
            streetError:'',
            isTouchedStreet: false,

            city: '',
            cityError:'',
            isTouchedCity: false,

            state: '',
            stateError:'',
            isTouchedState: false,

            account_nbr: '',
            account_nbrError:'',
            isTouchedAccount_nbr: false,

            FACTA: '',
            FACTAError:'',
            isTouchedFACTA: false,
        }


        this.handleNameBlur = this.handleNameBlur.bind(this);
        this.handleNameChange = this.handleNameChange.bind(this);

        this.handleTinBlur = this.handleTinBlur.bind(this);
        this.handleTinChange = this.handleTinChange.bind(this);

        this.handleZipcodeBlur = this.handleZipcodeBlur.bind(this);
        this.handleZipcodeChange = this.handleZipcodeChange.bind(this);

        this.handleStreetBlur = this.handleStreetBlur.bind(this);
        this.handleStreetChange = this.handleStreetChange.bind(this);

        this.handleCityBlur = this.handleCityBlur.bind(this);
        this.handleCityChange = this.handleCityChange.bind(this);

        this.handleStateBlur = this.handleStateBlur.bind(this);
        this.handleStateChange = this.handleStateChange.bind(this);


        this.handleAccount_nbrBlur = this.handleAccount_nbrBlur.bind(this);
        this.handleAccount_nbrChange = this.handleAccount_nbrChange.bind(this);


        this.handleFACTABlur = this.handleFACTABlur.bind(this);
        this.handleFACTAChange = this.handleFACTAChange.bind(this);


    }






    handleNameBlur(e) {
        const value = e.target.value;   
        this.setState({ name:value, isTouchedName:true, isValidName:(value.length > 0)});
        recipientValidator();
    };

    handleTINBlur(e) {
        const value = e.target.value;   
        this.setState({ tin:value, isTouchedTIN:true, isValidTIN:(value.length > 0)});
        recipientValidator();
    };

    handleAddressBlur(e) {
        const value = e.target.value;   
        this.setState({ street:value, isTouchedAddress:true, isValidAddress:(value.length > 3)});
        recipientValidator();
    };

    handleZipBlur(e) {
        const value = e.target.value;   
        this.setState({ zip:value, isTouchedZip:true, isValidZip:(value.length > 0)});
        recipientValidator();
    };

    handleAccountBlur(e) {
        const value = e.target.value;   
        this.setState({ account:value, isTouchedAccount:true });
        recipientValidator();
    };

    handleFATCABlur(e) {
        const value = e.target.value;   
        this.setState({ FATCA:value, isTouchedFATCA:true });
        recipientValidator();
    };

    handleFieldChange(e, fieldname) {
        const value = e.target.value;     
        switch(fieldname) {
            case 'name': 
                this.setState({name:value, isTouchedName:true, isValidName:(value.length > 0)}, () => {
                    this.props.updateRecipient(this.state)
                });
                break;
            case 'street': 
                this.setState({street:value, isTouchedAddress: true, isValidAddress:(value.length > 0)}, () => {
                    this.props.updateRecipient(this.state)
                });
                break;
            case 'ein': 
                var ein = e.target.value;  
                (ein.length === 9) ? this.setState({isValidTIN: true}) : this.setState({isValidTIN: false});
                this.setState({ tin: ein, isTouchedTIN: true }, () => {
                    this.props.updateRecipient(this.state)
                });
                break;
            case 'account': 
                this.setState({ account:value, isTouchedAccount: true }, () => {
                    this.props.updateRecipient(this.state)
                });
                break;
            case 'FATCA': 
                this.setState({FATCA:value, isTouchedFATCA: true }, () => {
                    this.props.updateRecipient(this.state)
                });
                break;
            default:;
        }
    }

    handleZipChange(e) {
        var zip = "";
        zip = e.target.value;
        this.setState({isTouchedZip: false});
        var zipURL = "http://ziptasticapi.com/" + zip
        if (zip.length === 5) {
            fetch(zipURL)
            .then(response => response.json())
            .then(data => {
                this.setState({ 
                    city: data.city,
                    state: data.state,
                    zipcode: zip,
                    isValidZip: true
                });
                // if (data.city === undefined) {
                //     this.setState({isValidZip: false})
                // }
            }).then( () => {
                this.props.updateRecipient(this.state)
            })
        } else {
            this.setState({isValidZip: false});
        }
    }

    renderToolTip = (props) => {
        return(
            <ReactTooltip className='tooltip-container' id={props.input} place="right" type="dark" effect="solid">
                 {props.tip}
            </ReactTooltip>
        )
    };



    render() {            
        const nameColor = (!this.state.isValidName || (this.recipientErrors && this.recipientErrors.name)) ? {border:'1px solid red'} : null;
        const einColor = (!this.state.isValidTIN || (this.recipientErrors && this.recipientErrors.tin)) ? {border:'1px solid red'} : null;
        const addressColor = (!this.state.isValidAddress || (this.recipientErrors && this.recipientErrors.street)) ? {border:'1px solid red'} : null;
        const zipColor = (!this.state.isValidZip || (this.recipientErrors && this.recipientErrors.zip)) ? {border:'1px solid red'} : null;
        const accountColor = this.state.isRequiredAccount && !this.state.isValidAccount ? {border:'1px solid red'} : null;


        return (
        <div>
            <div className="input-group" align="left">
                <h4>Recipient Information</h4>
            </div>


            <div align="left">
                <label>Recipient Name *</label>
                <div className="input-group">
                    <input type="text" className="form-control" id="RecipientName" required
                        value={this.state.name}
                        style={nameColor}
                        onChange={(e) => this.handleFieldChange(e, "name")} 
                        onBlur={(e) => this.handleNameBlur(e)}
                        data-for='RecipientName' data-tip data-event='focus' data-event-off='blur'
                    >
                    </input>
                    {this.renderToolTip( {tip:"If the recipient is an individual enter the their first and last name.  If the recipient is a company, enter the company name.  This field is required.", input: "RecipientName"} )}
                </div>
                <FieldCheck3 isValid={this.state.isValidName} message={this.state.recipientErrors.name} />
            </div>


            <div align="left">
                <label>Tax Identification Number * XXX-XX-XXXX</label>
                <div className="input-group">
                      <input type="number" className="form-control" id="inputSSN" required
                        value={this.state.tin}
                        style={einColor}
                        onChange={(e) => this.handleFieldChange(e, "ein")} 
                        onBlur={(e) => this.handleTINBlur(e)}
                        data-for='inputSSN' data-tip data-event='focus' data-event-off='blur'
                      />
                    {this.renderToolTip( {tip:"Enter the recipient's tax identification number.  For a company, use their EIN.  For an individual, use their SSN, ATIN or ITIN.  This field is required.", input: "inputSSN"} )}
            	</div>
                <FieldCheck3 isValid={this.state.isValidTIN} message={this.state.recipientErrors.tin} />
            </div>


            <div align="left">
                <label>Recipient Address *</label>
                <div className="form-group" align="left">
    				<div>
    				  <input type="text" className="form-control" id="inputRecipientAddress" required
                        value={this.state.street}
                        style={addressColor}
                        onChange={(e) => this.handleFieldChange(e, 'street')} 
                        onBlur={(e) => this.handleAddressBlur(e)}
                        data-for='inputRecipientAddress' data-tip data-event='focus' data-event-off='blur'
                      />
                      {this.renderToolTip( {tip:"Enter the recipient's street address.  This field is required.", input: "inputRecipientAddress"} )}
    				</div>
    			</div>
                <FieldCheck3 isValid={this.state.isValidAddress} message={this.state.recipientErrors.street} />
            </div>


            <div align="left">
                <label>Recipient City</label>
                <div className="form-group" align="left">
    				<div>
                      <input type="text" className="form-control" id="inputRecipientCity" value={this.state.city} readOnly />
    				</div>
    			</div>
            </div>


            <div class="form-row" align="left">
                <div class="col">
                    <label>State</label>
                    <div className="input-group" >
                        <input type="text" className="form-control" id="inputRecipientState" value={this.state.state} readOnly/>
                    </div>
                </div>
                <div class="col">
                    <label>Zipcode *</label>
                    <div className="input-group" >
                        <input type="number" id="inputRecipientZip" className="form-control" 
                            style={zipColor}
                            onChange={(event) => this.handleZipChange(event)} 
                            onBlur={(e) => this.handleZipBlur(e)}
                            data-for='inputRecipientZip' data-tip data-event='focus' data-event-off='blur'
                        />
                        {this.renderToolTip( {tip:"Enter the recipient's Zipcode.  Required.", input: "inputRecipientZip"} )}
                    </div>
                    <FieldCheck3 isValid={this.state.isValidZip} message={this.state.recipientErrors.zip} />
                </div>
            </div>


            <div align="left">
                <label>Account Number</label>
                <div className="form-group" align="left">
    				<div>
                      <input type="text" className="form-control" id="inputRecipientAccount" 
                        style={accountColor}
                        onChange={(e) => this.handleFieldChange(e, "account")} 
                        onBlur={(e) => this.handleAccountBlur(e)}
                        data-for='inputRecipientAccount' data-tip data-event='focus' data-event-off='blur'
                        />
    				</div>
                    {this.renderToolTip( {tip:"Enter the recipient's account number.  This field is required if the FATCA checkbox is checked.", input: "inputRecipientAccount"} )}
    			</div>
                <FieldCheck3 isValid={this.state.isValidAccount} message={this.state.recipientErrors.account} />
            </div>


            <div align="left" >
                <label>FATCA</label>
                <div className="input-group mb-3">
                    <div className="input-group-prepend">
                        <div className="input-group-text">
                            <input type="checkbox" aria-label="Checkbox for following text input"
                                onChange={(e) => this.handleFieldChange(e, "FATCA")} 
                                onBlur={(e) => this.handleFATCABlur(e)}  
                            />
                        </div>
                    </div>
                    <input type="readonly" className="form-control" id="chkbxFATCA"
                        data-for='inputFATCA' data-tip data-event='focus' data-event-off='blur'
                    />
                    {this.renderToolTip( {tip:"Foreign Account Tax Compliance Act.  Check the checkbox if you are a U.S. payer that is reporting per Chapter 4 of Regulations section 1.1471-4(d)(2)(iii)(A), or if you are a Foreign Financial Institution (FFI) reporting payments to a U.S. account.", input: "inputFATCA"} )}
                </div>
            </div>


        </div>
    )}
}


function mapStateToProps(state) {
    return{
        recipient: state.recipient
    }
}

const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({
        updateRecipient: updateRecipient
    }, dispatch )
}

GetRecipientInfo = connect(mapStateToProps, mapDispatchToProps)(GetRecipientInfo);

export default GetRecipientInfo;
