import React from 'react';
import ReactTooltip from 'react-tooltip'

import isEmail from 'validator/lib/isEmail';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';

import { updateEmail } from '../../actions';
import { FieldCheck3 } from './FieldCheck';

import store from '../../store';
import emailValidator from '../../validate/emailValidator';


class GetEmail extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            email: '',
            isValidEmail: true,
            isTouchedEmail: false,
        }
        this.handleEmailBlur = this.handleEmailBlur.bind(this);
        this.handleFieldChange = this.handleFieldChange.bind(this);
    }


    renderToolTip = (props) => {
        return(
            <ReactTooltip id={props.input} place="right" type="dark" effect="solid">
                 {props.tip}
            </ReactTooltip>
        )
    };



    handleEmailBlur(e) {
        var value = e.target.value; 
        this.setState({ email: value }, () => {
            this.props.updateEmail(this.state)
        });

        //Check if valid email  
        this.setState({ isValidEmail: isEmail(value) });
        emailValidator();
    };

    handleFieldChange(e) {
        //Update Email Value
        this.setState({ email:e.target.value, isTouchedEmail:true }, () => {
            this.props.updateEmail(this.state)
        });
    }



    render() {
        const emailColor = (!this.state.isValidEmail || (this.props.emailErrors && this.props.emailErrors.email)) ? {border: '1px solid red'} : null;

        return(
            <div align="left">
                <label>Email address *</label>
                <div className="form-group" align="left" >
                    <input type="email" className="form-control" id="inputEmail" 
                        style={emailColor}
                        onChange={(e) => this.handleFieldChange(e)} 
                        onBlur={(event) => this.handleEmailBlur(event)} 
                        required
                        data-for='inputEmail' data-tip data-event='focus' data-event-off='blur'
                    />
                    {this.renderToolTip( {tip:"This is the email address to which your receipt will be sent.", input: "inputEmail"} )}
                </div>
                <FieldCheck3 isValid={this.state.isValidEmail} message={this.props.emailErrors.email} />
            </div>
        )
    }
}


function mapStateToProps(state) {
    return{
      email: state.email,
      emailErrors: state.emailErrors,
    }
  }

const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({
        updateEmail: updateEmail
    }, dispatch )
  }

GetEmail = connect(mapStateToProps, mapDispatchToProps)(GetEmail);

export default GetEmail;
