import React from 'react';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import ReactTooltip from 'react-tooltip'

import { updateIncome } from '../../actions';


const toolTipText = [
    { field: 'rent', tip: 'Enter any rent paid to the recipient in this field.'},
    { field: 'royalties', tip: 'Enter any royalties paid to the recipient in this field.'},
    { field: 'other', tip: 'Enter any other income paid to the recipient in this field.'},
    { field: 'federal_tax_withheld', tip: 'Enter the amount of Federal income tax withheld from disbursements to recipient in this field.'},
    { field: 'non_employee_comp', tip: 'Enter any non-employee compensation paid to the recipient in this field.  This will be reported in Box 7.     This includes professional service fees, such as fees to attorneys (including corporations), accountants, architects, contractors, engineers, designers, software developers (if not paid as employees), etc.'},
    { field: 's409a_income', tip: 'Enter any Section 409A income paid to the recipient in this field..'},
    { field: 's409a_deferrals', tip: 'Enter any Section 409A deferrals paid to the recipient in this field.'},
    { field: 'direct_sales', tip: 'Payer made direct sales of $5,000 or more of consumer products to recipient for sale.'},
    { field: 'crop_insurance', tip: 'Enter any other income paid to the recipient in this field.'},
    { field: 'state_tax_withheld', tip: 'Enter the amount state income tax withheld in this field.'},
    { field: 'state_tax_id', tip: 'Enter the payer\'s or employer\'s state tax ID in this field.'},
    { field: 'state_income', tip: 'Enter the recipients state income in this field.'},
]


class GetIncome extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

            rent: '',
            rentError:'',
            isTouchedRent: false,


            royalties: '',
            royaltiesError:'',
            isTouchedRoyalties: false,


            other: '',
            otherError:'',
            isTouchedOther: false,


            federal_tax_withheld: '',
            federal_tax_withheldError:'',
            isTouchedFederal_tax_withheld: false,


            non_employee_comp: '',
            non_employee_compError:'',
            isTouchedNon_employee_comp: false,


            s409a_income: '',
            s409a_incomeError:'',
            isTouchedS409a_income: false,


            s409a_deferrals: '',
            s409a_deferralsError:'',
            isTouchedS409a_deferrals: false,


            fish_boat: '',
            fish_boatError:'',
            isTouchedFish_boat: false,


            medical_payments: '',
            medical_paymentsError:'',
            isTouchedMedical_payments: false,


            substitute_payments: '',
            substitute_paymentsError:'',
            isTouchedSubstitute_payments: false,


            direct_sales: '',
            direct_salesError:'',
            isTouchedDirect_sales: false,


            state_tax_withheld: '',
            state_tax_withheldError:'',
            isTouchedState_tax_withheld: false,


            state_tax_id: '',
            state_tax_idError:'',
            isTouchedState_tax_id: false,
        }


        this.handleRentBlur = this.handleRentBlur.bind(this);
        this.handleRentChange = this.handleRentChange.bind(this);

        this.handleRoyaltiesBlur = this.handleRoyaltiesBlur.bind(this);
        this.handleRoyaltiesChange = this.handleRoyaltiesChange.bind(this);

        this.handleOtherBlur = this.handleOtherBlur.bind(this);
        this.handleOtherChange = this.handleOtherChange.bind(this);

        this.handleFederal_tax_withheldBlur = this.handleFederal_tax_withheldBlur.bind(this);
        this.handleFederal_tax_withheldChange = this.handleFederal_tax_withheldChange.bind(this);

        this.handleNon_employee_compBlur = this.handleNon_employee_compBlur.bind(this);
        this.handleNon_employee_compChange = this.handleNon_employee_compChange.bind(this);

        this.handleS409a_incomeBlur = this.handleS409a_incomeBlur.bind(this);
        this.handleS409a_incomeChange = this.handleS409a_incomeChange.bind(this);

        this.handleFish_boatBlur = this.handleFish_boatBlur.bind(this);
        this.handleFish_boatChange = this.handleFish_boatChange.bind(this);

        this.handleMedical_paymentsBlur = this.handleMedical_paymentsBlur.bind(this);
        this.handleMedical_paymentsChange = this.handleMedical_paymentsChange.bind(this);

        this.handleSubstitute_paymentsBlur = this.handleSubstitute_paymentsBlur.bind(this);
        this.handleSubstitute_paymentsChange = this.handleSubstitute_paymentsChange.bind(this);

        this.handleDirect_salesBlur = this.handleDirect_salesBlur.bind(this);
        this.handleDirect_salesChange = this.handleDirect_salesChange.bind(this);

        this.handleState_tax_withheldBlur = this.handleState_tax_withheldBlur.bind(this);
        this.handleState_tax_withheldChange = this.handleState_tax_withheldChange.bind(this);

        this.handleState_tax_idBlur = this.handleState_tax_idBlur.bind(this);
        this.handleState_tax_idChange = this.handleState_tax_idChange.bind(this);

    }


    handleRentChange(e) {
        this.setState({ rent:e.target.value, isTouchedRent:true }, () => {
            this.props.updateIncome(this.state)
        });
    }

    handleRentBlur(e) {
        let value = e.target.value; 
        let errorMessage


        this.setState({rentError:errorMessage})
    };



    handleRoyaltiesChange(e) {
        this.setState({ royalties:e.target.value, isTouchedRoyalties:true }, () => {
            this.props.updateIncome(this.state)
        });
    }

    handleRoyaltiesBlur(e) {
        let value = e.target.value; 
        let errorMessage


        this.setState({royaltiesError:errorMessage})
    };



    handleOtherChange(e) {
        this.setState({ other:e.target.value, isTouchedOther:true }, () => {
            this.props.updateIncome(this.state)
        });
    }

    handleOtherBlur(e) {
        let value = e.target.value; 
        let errorMessage


        this.setState({otherError:errorMessage})
    };



    handleFederal_tax_withheldChange(e) {
        this.setState({ federal_tax_withheld:e.target.value, isTouchedFederal_tax_withheld:true }, () => {
            this.props.updateIncome(this.state)
        });
    }

    handleFederal_tax_withheldBlur(e) {
        let value = e.target.value; 
        let errorMessage


        this.setState({federal_tax_withheldError:errorMessage})
    };



    handleNon_employee_compChange(e) {
        this.setState({ non_employee_comp:e.target.value, isTouchedNon_employee_comp:true }, () => {
            this.props.updateIncome(this.state)
        });
    }

    handleNon_employee_compBlur(e) {
        let value = e.target.value; 
        let errorMessage


        this.setState({non_employee_compError:errorMessage})
    };



    handleNon_employee_compChange(e) {
        this.setState({ non_employee_comp:e.target.value, isTouchedNon_employee_comp:true }, () => {
            this.props.updateIncome(this.state)
        });
    }

    handleNon_employee_compBlur(e) {
        let value = e.target.value; 
        let errorMessage


        this.setState({non_employee_compError:errorMessage})
    };





    getToolTip(props) {
        // filter toolTipText array to isolate the one in props
        const tip = toolTipText.filter( el => el.field === props );
        if (tip[0]) {
            return <div> {tip[0].tip} </div> ;
        }

        return <div> Please enter the appropriate information </div> ;
    };


    renderToolTip = (props) => {
        return(
            <ReactTooltip className='tooltip-container' id={props.input} place="right" type="dark" effect="solid">
                 {this.getToolTip(props.field) }
            </ReactTooltip>
        )
    };





    render() {
    return (
        <div>
            <div className="input-group" align="left">
                <h4>Income Information</h4>
            </div>


            <div align="left">
                <label>Rent</label>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">$</span>
                    </div>
                    <input type="number" id="inputRent" className="form-control" 
                        onChange={(e) => this.handleFieldChange(e, "rent")} 
                        onBlur={(e) => this.handleRentBlur(e)}
                        data-for='inputRent' data-tip data-event='focus' data-event-off='blur'
                        />
                    {this.renderToolTip( {field:"rent", input: "inputRent"} )}
                </div>
            </div>


            <div align="left">
                <label>Royalties</label>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">$</span>
                    </div>
                    <input className="form-control" type="number" id="inputRoyalties" 
                        onChange={(e) => this.handleFieldChange(e, "royalties")} 
                        onBlur={(e) => this.handleRoyaltiesBlur(e)}
                        data-for='inputRoyalties' data-tip data-event='focus' data-event-off='blur'
                    />
                    {this.renderToolTip( {field:"royalties", input: "inputRoyalties"} )}
                </div>
            </div>


            <div align="left">
                <label>Other Income</label>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">$</span>
                    </div>
                    <input className="form-control" type="number" id="inputOtherIncome" 
                        onChange={(e) => this.handleFieldChange(e, "other")} 
                        onBlur={(e) => this.handleOtherBlur(e)}
                        data-for='inputOtherIncome' data-tip data-event='focus' data-event-off='blur'
                    />
                    {this.renderToolTip( {field:"other", input: "inputOtherIncome"} )}
                </div>
            </div>


            <div align="left">
                <label>Federal Income Tax Withheld</label>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">$</span>
                    </div>
                    <input type="number" className="form-control" id="inputFedTax" 
                        onChange={(e) => this.handleFieldChange(e, "federal_tax_withheld")} 
                        onBlur={(e) => this.handleFedTaxBlur(e)}
                        data-for='inputFedTax' data-tip data-event='focus' data-event-off='blur'
                    />
                    {this.renderToolTip( {field:"federal_tax_withheld", input: "inputFedTax"} )}
                </div>
            </div>


            <div align="left">
                <label>Non-Employee Compensation</label>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">$</span>
                    </div>
                    <input type="number" className="form-control" id="inputNonEmployee"
                        onChange={(e) => this.handleFieldChange(e, "non_employee_comp")} 
                        onBlur={(e) => this.handleNonEmpCompBlur(e)}
                        data-for='inputNonEmployee' data-tip data-event='focus' data-event-off='blur'
                    />
                    {this.renderToolTip( {field:"non_employee_comp", input: "inputNonEmployee"} )}
                </div>
            </div>


            <div align="left">
                <label>Section 409A Income</label>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">$</span>
                    </div>
                    <input type="number" id="input409Income" className="form-control"  
                        onChange={(e) => this.handleFieldChange(e, "s409a_income")} 
                        onBlur={(e) => this.handleS409IncBlur(e)}
                        data-for='input409Income' data-tip data-event='focus' data-event-off='blur'
                    />
                    {this.renderToolTip( {field:"s409a_income", input: "input409Income"} )}
                </div>
            </div>


            <div align="left">
                <label>Section 409A Deferrals</label>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">$</span>
                    </div>
                    <input type="number" id="input409Deferrals" className="form-control" 
                        onChange={(e) => this.handleFieldChange(e, "s409a_deferrals")} 
                        onBlur={(e) => this.handleS409DefBlur(e)}
                        data-for='input409Deferrals' data-tip data-event='focus' data-event-off='blur'
                    />
                    {this.renderToolTip( {field:"s409a_deferrals", input: "input409Deferrals"} )}
                </div>
            </div>


            <div align="left">
                <label>State Tax Withheld</label>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">$</span>
                    </div>
                    <input type="number" id="inputStateTax" className="form-control" 
                        onChange={(e) => this.handleFieldChange(e, "state_tax_withheld")} 
                        onBlur={(e) => this.handleNonEmpCompBlur(e)}
                        data-for='inputStateTax' data-tip data-event='focus' data-event-off='blur'
                    /> 
                    {this.renderToolTip( {field:"state_tax_withheld", input: "inputStateTax"} )}
                </div>
            </div>


            <div align="left">
                <label>Payer's State Tax ID</label>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">12-3...</span>
                    </div>
                    <input type="number" id="inputStateTaxID" className="form-control" 
                        onChange={(e) => this.handleFieldChange(e, "state_tax_id")} 
                        onBlur={(e) => this.handleNonEmpCompBlur(e)}
                        data-for='inputStateTaxID' data-tip data-event='focus' data-event-off='blur'
                    />
                    {this.renderToolTip( {field:"state_tax_id", input: "inputStateTaxID"} )}
                </div>
            </div>


            <div align="left">
                <label>State Income</label>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <span className="input-group-text">$</span>
                    </div>
                    <input type="number" id="inputStateIncome" className="form-control" 
                        onChange={(e) => this.handleFieldChange(e, "state_income")} 
                        onBlur={(e) => this.handleNonEmpCompBlur(e)}
                        data-for='inputStateIncome' data-tip data-event='focus' data-event-off='blur'
                    />
                    {this.renderToolTip( {field:"state_income", input: "inputStateIncome"} )}
                </div>
            </div>


            <div align="left">
                <label>Direct sales of $5,000 or more</label>
                <div className="input-group mb-3" >
                    <div className="input-group-prepend">
                        <div className="input-group-text">
                            <input type="checkbox" aria-label="Checkbox for following text input"
                                onChange={(e) => this.handleFieldChange(e, "direct_sales")} 
                                onBlur={(e) => this.handleDirectSalesBlur(e)}
                                data-for='chkbxDirectSales' data-tip data-event='focus' data-event-off='blur'
                            />
                        </div>
                    </div>
                    <input type="readonly" className="form-control" id="chkbxDirectSales" />
                    {this.renderToolTip( {field:"direct_sales", input: "chkbxDirectSales"} )}
                </div>
            </div>

        </div>
    )
    }
}


function mapStateToProps(state) {
    return{
        payer: state.income
    }
}

const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({
        updateIncome: updateIncome
    }, dispatch )
}

GetIncome = connect(mapStateToProps, mapDispatchToProps)(GetIncome);

export default GetIncome;
