import React from 'react';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';

import blank1099 from '../../images/blank1099.png'; // local 1099

const canvg = require('canvg')
const jsPDF = require('jspdf')



class Preview1099 extends React.Component {


    createCanvas(){
        //SVG data
        var svg = document.getElementById('editor');
        var data = (new XMLSerializer()).serializeToString(svg);

        //Create canvas
        var canvas = document.getElementById('canvas');
        canvg('canvas', data, {useCORS: true});
    }


    downloadPDF(){
        var canvas = document.getElementById('canvas');
        var imgData = canvas.toDataURL("image/jpeg", 1.0);

        //Create PDF
        var pdf = new jsPDF({
             orientation: 'p',
             unit: 'in',
             format: 'letter',
        });

        //Add Image at Scale
        pdf.addImage(imgData, 'JPEG', 0, 0, 8.5, 6);
        pdf.save("1099.pdf");
    }




  render() {


    console.log('payer', this.props.payer)
    console.log('tax year', this.props.taxYear)


    let payer = {
        name:'Joe Test',

        street:'123 Fake Street',
        city:'Fakeland',
        state:'CA',
        country:'USA',
        zipcode:'92822',
        phone:'888-888-8888',

        tin:'123412341234',
    }

    let recipient = {
        name:'Fred Test',

        street:'123 Fake Street',
        city:'Fakeland',
        state:'CA',
        country:'USA',
        zipcode:'92822',

        tin:'123412341234',

    }

    let income = {
        FACTA:true,

        account_nbr:'123412341234',

        rent:'1234.12',
        royalties:'1234.12',
        other:'1234.12',

        federal_tax_withheld:'1234.12',
        non_employee_comp:'1234.12',

        s409a_deferrals:'1234.12',
        s409a_income:'1234.12',

        fish_boat:'1234.12',
        medical_payments:'1234.12',
        substitute_payments:'1234.12',

        direct_sales:true,

        crop_ins:'1234.12',
        golden:'1234.12',
        attorney:'1234.12',

        state_tax_withheld:'1234.12',
        state_tax_id:'12341234',
        state_income:'1234.12'
    }

    let fontStyle = {fontSize:'12px', fontWeight:'600'}
    let fontColor = 'blue'

    let formImage = blank1099

    let imageWidth = '883px'
    let imageHeight = '600px'


    return (
            <div>

                <svg  height={imageHeight} width={imageWidth} id='editor' xmlnsXlink="http://www.w3.org/1999/xlink">
                    <image xmlnsXlink="http://www.w3.org/1999/xlink" xlinkHref={formImage} x="0" y="0" height={imageHeight} width={imageWidth}/>


                    <text x="60" y="100" fill={fontColor} style={fontStyle}>{payer.name}</text>
                    <text x="60" y="125" fill={fontColor} style={fontStyle}>{payer.street}</text>
                    <text x="60" y="150" fill={fontColor} style={fontStyle}>{payer.city}, {payer.state} {payer.country} {payer.zipcode}</text>
                    <text x="60" y="175" fill={fontColor} style={fontStyle}>{payer.phone}</text>
                    <text x="60" y="240" fill={fontColor} style={fontStyle}>{payer.tin}</text>
                    <text x="250" y="240" fill={fontColor} style={fontStyle}>{recipient.tin}</text>
                    <text x="60" y="305" fill={fontColor} style={fontStyle}>{recipient.name}</text>
                    <text x="60" y="360" fill={fontColor} style={fontStyle}>{recipient.street}</text>
                    <text x="60" y="415" fill={fontColor} style={fontStyle}>{recipient.city}, {recipient.state} {recipient.country} {recipient.zipcode}</text>
                    <text x="60" y="470" fill={fontColor} style={fontStyle}>{income.account_nbr}</text>
                    <text x="314" y="478" fill={fontColor} style={fontStyle}>{income.FACTA ? 'X' : null}</text>
                    <text x="60" y="540" fill={fontColor} style={fontStyle}>{income.s409a_deferrals}</text>
                    <text x="250" y="540" fill={fontColor} style={fontStyle}>{income.s409a_income}</text>


                    <text x="440" y="95" fill={fontColor} style={fontStyle}>{income.rent}</text>
                    <text x="440" y="150" fill={fontColor} style={fontStyle}>{income.royalties}</text>
                    <text x="440" y="190" fill={fontColor} style={fontStyle}>{income.other}</text>
                    <text x="440" y="260" fill={fontColor} style={fontStyle}>{income.fish_boat}</text>
                    <text x="440" y="335" fill={fontColor} style={fontStyle}>{income.non_employee_comp}</text>
                    <text x="553" y="390" fill={fontColor} style={fontStyle}>{income.direct_sales? 'X' : null}</text>
                    <text x="440" y="485" fill={fontColor} style={fontStyle}>{income.golden}</text>
                    <text x="440" y="520" fill={fontColor} style={fontStyle}>{income.state_tax_withheld}</text>

                    <text x="600" y="190" fill={fontColor} style={fontStyle}>{income.federal_tax_withheld}</text>
                    <text x="600" y="260" fill={fontColor} style={fontStyle}>{income.fish_boat}</text>
                    <text x="600" y="335" fill={fontColor} style={fontStyle}>{income.substitute_payments}</text>
                    <text x="600" y="390" fill={fontColor} style={fontStyle}>{income.crop_ins}</text>
                    <text x="600" y="485" fill={fontColor} style={fontStyle}>{income.attorney}</text>
                    <text x="600" y="520" fill={fontColor} style={fontStyle}>{income.state_tax_id}</text>

                    <text x="750" y="520" fill={fontColor} style={fontStyle}>{income.state_income}</text>

                </svg>

                <br/>
                <button className='btn btn-lg btn-outline-primary' onClick={this.createCanvas.bind(this)}>Create 1099</button>
                <button className='btn btn-lg btn-primary' onClick={this.downloadPDF.bind(this)}>Download 1099 PDF</button>

                <canvas style={{display:'none'}} id='canvas' height={imageHeight} width={imageWidth} ></canvas>

            </div>
    );
  }
}

function mapStateToProps(state) {
  return{
      taxYear: state.taxYear,
      payer: state.payer,
      recipient: state.recipient,
      income: state.income,
      email: state.email
  }
}

const mapDispatchToProps = (dispatch) => {
  return bindActionCreators({ 

  }, dispatch )
}

Preview1099 = connect(mapStateToProps, mapDispatchToProps)(Preview1099);

export default Preview1099;
